    const [loading, setLoading] = useState(true);
    const [hasToken, setHasToken] = useState(false);

    useEffect(() => {
        const check = async () =>{
                if(localStorage.getItem("DeviceToken")!==null){
                  setLoading(false);
                  return <Redirect to='/Start'/>;
                }else{
                  setLoading(false);
                  return <Redirect to='/Start'/>;
                }
            };
            check();
        },[]);



    return (
      <div>
      {loading && <div className="absoluteCenter"> <img src={anim}/></div>}
      </div>

    );
}


